import Vue from "vue";
import Vuex from "vuex";
import { fetchNewsList } from '../api/index.js';


Vue.use(Vuex);

export const store = new Vuex.Store({
    state: {
        news: [],
        asks: [],
        jobs : [],
    },
    mutations: {
        SET_NEWS(state) {

        }
    },
    actions: {
        FETCH_NEWS(context) {
            fetchNewsList()
                .then(response => context.commit(SET_NEWS(response.data)))
                .catch()
        }
    }
})
