import Vue from "vue";
import Vuex from "vuex";
import { fetchNewsList } from '../api/index.js';


Vue.use(Vuex);

export const store = new Vuex.Store({
    state: {
        news: [],
        asks: [],
        jobs : [],
    },
    actions: {
        FETCH_NEWS(state) {
            fetchNewsList()
                .then(response => state.news = response.data)
                .catch()
        }
    }
})
