import Vue from "vue";
import Vuex from "vuex";
import { fetchNewsList, fetchJobsList } from '../api/index.js';


Vue.use(Vuex);

export const store = new Vuex.Store({
    state: {
        news: [],
        asks: [],
        jobs : [],
    },
    mutations: {
        SET_NEWS(state, news) {
            state.news = news;
        }
    },
    actions: {
        FETCH_NEWS(context) {
            fetchNewsList()
                .then(response => context.commit('SET_NEWS', response.data))
                .catch()
        },
        FETCH_JOBS(context) {
            fetchJobsList()
                .then(response => context.commit('', response.data))
                .catch(error => console.log(error))
        }
    }
})
